package lc_1371;

import java.util.Arrays;
import java.util.HashMap;
import java.util.Map;

/*
 *@author: 黄豪
 *@date : 2022年1月18日
 *@todo:1371. 每个元音包含偶数次的最长子字符串
给你一个字符串 s ，请你返回满足以下条件的最长子字符串的长度：每个元音字母，即 'a'，'e'，'i'，'o'，'u' ，在子字符串中都恰好出现了偶数次。

 

示例 1：

输入：s = "eleetminicoworoep"
输出：13
解释：最长子字符串是 "leetminicowor" ，它包含 e，i，o 各 2 个，以及 0 个 a，u 。
示例 2：

输入：s = "leetcodeisgreat"
输出：5
解释：最长子字符串是 "leetc" ，其中包含 2 个 e 。
*/
public class LC_1371 {
	public static void main(String[] args) {
		System.out.println(new Solution1().findTheLongestSubstring(""));
	}
}
//深度优先搜索(超时)
class Solution1 {
    public int findTheLongestSubstring(String s) {
        return dfs(s);
    }
    private int dfs(String s){
        int n = s.length();
        Map<Character, Integer> map = new HashMap<>();
        map.put('a', 0);
        map.put('e', 0);
        map.put('i', 0);
        map.put('o', 0);
        map.put('u', 0);
        for (int i = 0; i < n; i++){
            char c = s.charAt(i);
            if (map.containsKey(c)){
                map.put(c, map.get(c) ^ 1);
            }
        }
        char split = 0;
        for (Map.Entry<Character, Integer> entry : map.entrySet()){
            if (entry.getValue() == 1){
                split = entry.getKey();
                break;
            }
        }
        if (split == 0){
            return n;
        }
        int i = s.indexOf(split), j = s.lastIndexOf(split), ans = 0;
        int len = dfs(s.substring(0, i));
        int len2 = i != j ? Math.max(dfs(s.substring(i+1, n)), dfs(s.substring(0, j))): dfs(s.substring(i+1, n));
        ans = Math.max(ans, Math.max(len, len2));
        return ans;
    }
}
//前缀和
class Solution {
    public int findTheLongestSubstring(String s) {
        int n = s.length();
        int[] pos = new int[1 << 5];
        Arrays.fill(pos, -1);
        int ans = 0, status = 0;
        pos[0] = 0;
        for (int i = 0; i < n; i++) {
            char ch = s.charAt(i);
            if (ch == 'a') {
                status ^= (1 << 0);
            } else if (ch == 'e') {
                status ^= (1 << 1);
            } else if (ch == 'i') {
                status ^= (1 << 2);
            } else if (ch == 'o') {
                status ^= (1 << 3);
            } else if (ch == 'u') {
                status ^= (1 << 4);
            }
            if (pos[status] >= 0) {
                ans = Math.max(ans, i + 1 - pos[status]);
            } else {
                pos[status] = i + 1;
            }
        }
        return ans;
    }
}